import { Component, EventEmitter, Output, NgModule } from '@angular/core';
import { UserService } from '../services/person.service';
import { Person } from '../services/person';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-account',
  templateUrl: './new-account.component.html',
  styleUrls: ['./new-account.component.css']
})
export class NewAccountComponent {
  @Output() OnLoginClicked = new EventEmitter();

  router : Router;

  constructor(private service: UserService, router : Router) { 
    this.router = router;
  }

  protected rePassword = '';
  protected alertContent = '';
  protected alertDiv = false;
  protected alertLevel = 1;

  user : Person = {
    id : 0,
    name : '',
    birth : new Date,
    email : '',
    photo : '',
    password : '',
    salt : ''
  }

  checkPassword() {
    console.log(this.user.password === this.rePassword);

    return this.user.password === this.rePassword
  }

  validatePassword(newValue : string){
    this.rePassword = newValue
    if(!this.checkPassword())
    {
      this.alertDiv = true;
      this.alertContent = 'Passwords are different'
      this.alertLevel = 1
    }
  }

  signInClicked(){
    console.log(this.user + 'OKAY');

    if (!this.checkPassword()) {
      this.alertDiv = true;
      this.alertContent = 'Passwords are different'
      this.alertLevel = 2
      console.log(this.alertLevel);

      return
    }

    this.service.registerUser(this.user).subscribe(
      res => console.log(res)
    );
    this.router.navigate(['/login-page'])
  }


  loginClicked()
  {
    this.OnLoginClicked.emit();
  }
}
<div class="container">
  <div class="content">
    <h2>Create Account</h2>
    <div>
      <span id="image-input">
        <button>
          <img src="../../assets/images/person-standard-logo.png" width="100" alt="Image Preview">
        </button>
        <input type="file" id="profile-photo" accept="image/*">
      </span>
      <span>
        <!-- <label for="name">Name</label> -->
        <input [(ngModel)]="user.name" id="name" type="text" placeholder="Example Name" />
      </span>
      <span>
        <!-- <label for="email">Email</label> -->
        <input [(ngModel)]="user.email" id="email" type="email" placeholder="example@domain.com" />
      </span>
      <span>
        <!-- <label for="pass">Password</label> -->
        <input [(ngModel)]="user.password"  id="pass" type="password" placeholder="********" />
      </span>
      <span>
        <!-- <label for="re-pass">Confirm Password</label> -->
        <input [(ngModel)]="rePassword" (ngModelChange)="validatePassword($event)" id="re-pass" type="password" placeholder="********" />
      </span>
      <span>
        <!-- <label for="birth">Birthday</label> -->
        <input [(ngModel)]="user.birth" id="birth" type="date" placeholder="dd/mm/yyyy" />
      </span>
      <div class="buttons">
        <button (click)="loginClicked()">Login</button>
        <button (click)="signInClicked()">Sign In</button>
      </div>
    </div>
    <div id="alert" 
      [ngStyle]="{
        'background-color':'var(--alert-color' + alertLevel + ')',
        'display': alertDiv ? 'flex' : 'none'
      }">
      <p>{{ alertContent }}</p>
    </div>
  </div>
</div>
