import { Component } from '@angular/core';
import { ForumService } from '../services/forum.service';
import { Jwt } from '../services/person';
import { HttpErrorResponse } from '@angular/common/http';
import { Forum } from '../services/Model';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {
  constructor(private service: ForumService) {  }
  protected isLogged = false;
  jwt : Jwt = {
    value : sessionStorage.getItem('jwt') ?? ""
  }

  protected forumsListFollowed : Forum[] = [];

  ngOnInit(): void {
    console.log(this.jwt.value);
    if(this.jwt.value == "")
      this.isLogged = false
    else
      this.isLogged = true
    if(this.isLogged)
      this.getForumsFollowed();
  }

  getForumsFollowed (){
    return this.service
      .GetUserForums(this.jwt)
      .subscribe( item => {
        let list: Forum[] = []
        item.forEach(forums => {
          list.push(forums)
        })
        this.forumsListFollowed = list;
        console.log(this.forumsListFollowed);
      })
  }
}
