import { Component } from '@angular/core';
import { Forum } from '../services/Model';
import { ForumService } from '../services/forum.service';

@Component({
  selector: 'app-all-forums-page',
  templateUrl: './all-forums-page.component.html',
  styleUrls: ['./all-forums-page.component.css']
})
export class AllForumsPageComponent {
  protected allForums : Forum[] = []

  search = ''

  searchChanged(string : string){
    console.log(this.allForums);
    this.search = string;
    this.allForums = this.allForums.filter(x => x.Title == "Titanic");
    console.log(this.allForums);

  }

  constructor( private service : ForumService ){  }

  ngOnInit() : void  {
    this.getForums()
  }

  getForums(){
    this.service.GetAllForums()
      .subscribe( (item : any) => {
        let list : Forum[] = []
        item.forEach((element : any )=> {
          list.push(element);
        });
        this.allForums = list;
        console.log(this.allForums);

    })
  }
}
<div id="main">
    <app-menu></app-menu>
    <div class="content">
        <input type="text" (ngModelChange)="searchChanged($event)" [(ngModel)]="search">
        <app-forum-card *ngFor="let forum of this.allForums" [obj]="forum" ></app-forum-card>
    </div>
    <!-- <footer>
        <app-footer></app-footer>
    </footer> -->
</div>
