import { Component, EventEmitter, Output } from '@angular/core';
import { Person, LoginData } from '../services/person';
import { UserService } from '../services/person.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  @Output() onSingInClicked = new EventEmitter();

  constructor(private service: UserService) { }

  user : LoginData = {
    indentify : '',
    password : ''
  }

  signInClicked()
  {
    this.onSingInClicked.emit()
  }


  loginClicked()
  {
    this.service.sendUser(this.user)
  }
}
<div class="container">
  <div class="content">
    <h2>LOGIN</h2>
    <div>
      <span>
          <input [(ngModel)]="user.indentify" id="email" type="email" placeholder="Login" />
      </span>
      <span>
          <input [(ngModel)]="user.password" id="password" type="password" placeholder="Password" />
      </span>
      <div class="buttons">
          <button>Login</button>
          <button (click)="signInClicked()">Sign In</button>
      </div >
    </div>
  </div>
</div>
